mapper.select:
	/* 조인을 이용한 방법 */
	SELECT *
	FROM
	( SELECT ROWNUM RN, A.*
	FROM
	( /* 조인을 이용한 방법 */
	SELECT O_NO, O.PRICE, AMOUNT, QUANTITY, TO_CHAR(O_REGDATE,'YYYY-MM-DD')
	O_REGDATE, STATE,
	MEM_ID, (SELECT MEM_NAME FROM MEMBER WHERE MEM_ID=#{mem_id}) MEM_NAME, O.P_NO,
	P.IMAGE, P_NAME
	FROM ORDERT O INNER JOIN PRODUCT P
	ON O.P_NO = P.P_NO
	/* -- 서브쿼리 이용한 방법
	SELECT O_NO, O.PRICE, AMOUNT, QUANTITY, TO_CHAR(O_REGDATE,'YYYY-MM-DD')
	O_REGDATE, STATE,
	MEM_ID, O.P_NO, (SELECT P_NAME FROM PRODUCT WHERE P_NO=2) P_NAME
	ORDERT O*/
	WHERE mem_id = #{mem_id}
	ORDER BY O_REGDATE DESC
	) A
	)



       WHERE RN  >= #{start} AND RN <= #{end}




				WHERE P_NO = #{p_no}


				AND O_NO = #{o_no}

update.foreach:
UPDATE ORDERT SET
STATE = #{item.state},
O_REGDATE=SYSDATE
WHERE O_NO = #{item.o_no}
AND P_NO = #{item.p_no}
AND MEM_ID = #{item.mem_id}

mapper.update:
UPDATE ORDERT SET
STATE = #{state},
O_REGDATE=SYSDATE
WHERE O_NO = #{o_no}
AND P_NO = #{p_no}
AND MEM_ID = #{mem_id}

mapper.delete:
DELETE FROM ORDERT
WHERE O_NO =#{o_no}
AND P_NO = #{p_no}
AND MEM_ID = #{mem_id}
